import React from 'react';
import './VideoInfoBox.scss';
import {Image, Button} from 'semantic-ui-react';

export class VideoInfoBox extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			collapsed: true,
		};
	}

	render() {
		let descriptionTextClass = 'collapsed';
		let buttonTitle = 'Show More';
		if(!this.state.collapsed) {
			descriptionTextClass = 'expanded';
			buttonTitle = 'Show Less';
		}
		return (
			<div className='video-info-box'>
				<Image className='channel-mage' src='http://via.placeholder.com/48x48' circular/>
				<div className="video-info">
					<div className='channel-name'>Channel Name</div>
					<div className='video-publication-date'>4 20, 2020</div>
				</div>
				<Button color='youtube'>91.5k Subscribe</Button>
				<div className="video-description">
				  <p>Paragraph 1</p>
				  <p>Paragraph 2</p>
				  <p>Paragraph 3</p>
				  <p>Paragraph 4</p>
				  <p>Paragraph 5</p>
				  <Button compact >Show More</Button>
				</div>
			</div>
		);
	}

	onToggleCollapseButtonClick = () => {
		this.setState((prevState) => {
			return {
				collapsed: !prevState.collapsed
			};
		});
	}
}
